import React, {Component} from 'react';
import axios from "axios";
import App from '../App.js';

export default class SendSnap extends Component {

	constructor(props){
        super(props);
        this.state = {
        	users: [],
            file : '',
            dest : '',
            result : <div></div>

        };
        
    }

    setFile = (e) => {
        this.setState({file : e.target.value});
    };

    setDest = (e) => {
        this.setState({dest : e.target.value});
        
    };

    componentDidMount(){

        axios
        .get('http://snapi.epitech.eu/all', {headers : { token : localStorage.getItem('token')}})
        .then( value => {
         	this.setState({ users: value.data.data });
         	
     	})
        .catch(err => { console.log(err)})
    }

	render(){
		return(
			<div className="container">
                <div className='row'>
                    <div className="col-xl-5 col-sm-12 mx-auto d-block">
                        <form method="post" action="/send" onSubmit={console.log(this.state.dest)} >
                            <div className="form-group">
                                <label htmlFor="email">Image</label>
                                <input className="form-control" id="file" type="file" onChange={this.setFile} value={this.file}/>
                            </div>
                            <div className="form-group">
                            	<select className="form-control" id="users" name="users" onChange={this.setDest} value={this.dest} >
                            		<option value='' > A qui voulez vous envoyer cette image ? </option>
                            	{this.state.users.map( function(item,i){
                            		
                            		return <option id={i} value={item.email}> {item.email} </option> ;
                            		
                            	})}
                            	</select> 
                            </div>
                            <button className="btn btn-danger btn-lg mx-auto d-block mt-5" type="submit">Snap !
                            </button>
                        </form>
                    </div>
                </div>
            </div>
			);
	}


}
