import React, { Component } from 'react';
import axios from "axios";
import App from '../App.js';

class Login extends Component {

    constructor(props){
        super(props);
        this.state = {
            email : '',
            password : '',
            result : <div></div>

        };
    }

    setPassword = (e) => {
        this.setState({password : e.target.value});
    };

    setEmail = (e) => {
        this.setState({email : e.target.value});
    };

    login = (e) => {
		e.preventDefault();
		axios.post('http://snapi.epitech.eu/connection', this.state)
		.then(confirm => {
			localStorage.setItem('token', confirm.data.data.token);
			new App();
            this.props.history.push('/');
		})
		.catch(err => {
			this.setState({ result : <div className=" alert alert-danger"> Mauvais email ou mdp ! </div> });
		})
	}
	render(){
		return (
			<div className="container">
				<form method="POST" action="/connection" onSubmit={this.login}  >
					<div className="form-group">
						<label>Email</label>
					    <input type="email" className="form-control" onChange={this.setEmail} value={this.email} name="email" placeholder="email" required />
					</div>
					<div className="form-group">
						<label>Password</label>
					    <input type="password" className="form-control" onChange={this.setPassword} value={this.password} name="password" required />
					</div>
					<button type="submit" className="btn btn-primary">Submit</button>
				</form>
			</div>
			)
	}
}
export default Login;
