import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from "axios";
import App from '../App.js';

export default class Home extends Component {

	disconnect(){
		localStorage.removeItem('token');
                new App();
                

	}
	render(){
		if (localStorage.getItem('token') === null){
		return (
			<div className="container">
				<div className="title row align-items-center justify-content-center">
					<h1 className="display-3 mt-3"> Snapchat Web Version </h1>
				</div>

				<div className="row mt-5 align-items-center justify-content-center">
					<div className="col-sm text-center"> 
						<p>Vous avez un compte ? Connectez vous !</p>
						<Link to="/login" className="btn btn-primary ">Connection</Link>
					</div>
					<div className="col-sm text-center">
						<p>Vous n'avez pas de compte ? Inscrivez vous !</p>
						<Link to="/register" className="btn btn-primary ">Inscription</Link>
					</div>
				</div>
			</div>


			)
		}
		else{
			return(
				<div className="container">
				<div className="title row align-items-center justify-content-center">
					<h1 className="display-3 mt-3"> Snapchat Web Version </h1>
				</div>

				<div className="row mt-5 align-items-center justify-content-center">
					<div className="col-sm text-center"> 
						<p>Mon Profil</p>
						<Link to="/profil" className="btn btn-primary ">Profil</Link>
					</div>
					<div className="col-sm text-center">
						<p>Snapper !</p>
						<Link to="/send" className="btn btn-primary ">Snapper</Link>
					</div>
					<div className="col-sm text-center">
						<p>Snaps reçus !</p>
						<Link to="/snaps" className="btn btn-primary ">Mes snaps</Link>
					</div>
					<div className="col-sm text-center">
						<button className="btn btn-primary " onClick={this.disconnect}>Log Out</button>
					</div>

				</div>
			</div>
)
		}
	}
}
