import React, { Component } from 'react';
import axios from "axios";

class Login extends Component {

state = {
	email : '',
	password : '',
	result : '',
}

	login(){
		let mail = getElementByName('email').val();
		this.setState({ email : mail});
		let pass = getElementByName('email').val();
		this.setState({ password : pass});
		axios.post('http://snapi.epitech.eu/connection', this.state)
		.then(confirm => {
			localStorage.setItem('token', confirm.data.data.token);
			this.Redirect('/');
		})
		.catch(err => {
			this.setState({ result : <div className=" alert alert-danger"> Mauvais email ou mdp ! </div> });
		})
	}
	render(){
		return (
			<div className="container">
				<form method="POST" action="/connection" onSubmit={this.login}  >
					<div className="form-group">
						<label>Email</label>
					    <input type="email" className="form-control" name="email" placeholder="email" required />
					</div>
					<div className="form-group">
						<label>Password</label>
					    <input type="password" className="form-control" name="password" required />
					</div>
					<button type="submit" className="btn btn-primary">Submit</button>
				</form>
			</div>
			)
	}
}
export default Login;
